mapper.select:
SELECT
FROM ( SELECT package_id AS pid FROM applet_package_tag_filter WHERE 1=1
AND tag_id = #{tagId,jdbcType=VARCHAR}
GROUP BY pid ) b INNER JOIN ( SELECT
FROM applet_package WHERE 1=1
AND package_id = #{packageId,jdbcType=VARCHAR}
AND package_name LIKE CONCAT('%',#{packageName,jdbcType=VARCHAR},'%')
AND package_source_id = #{sourceId,jdbcType=VARCHAR}
AND sample_type = #{sampleType,jdbcType=INTEGER}
and package_type = 0 ) a ON a.package_id = b.pid

SELECT
FROM ( SELECT package_id AS packageIdForFilter FROM applet_package_tag_filter WHERE 1=1 AND del_flag = 0
AND tag_id = #{tagId,jdbcType=VARCHAR}
) a LEFT JOIN ( SELECT
FROM applet_package WHERE 1=1 del_flag = 0 and package_type = 0
AND package_name LIKE CONCAT('%', #{queryParam,jdbcType=VARCHAR}, '%')
) b ON a.packageIdForFilter = b.package_id
